bot = Bot(token=API_TOKEN)
dp = Dispatcher()

with open('questions.json', 'r', encoding='utf-8') as f:
    all_questions = json.load(f)

user_state = {}

def get_menu():
    return InlineKeyboardMarkup(inline_keyboard=[
        [InlineKeyboardButton(text="Yangi savol", callback_data="new")],
        [InlineKeyboardButton(text="Javobni ko‘rsat", callback_data="answer")]
    ])

@dp.message(Command("start"))
async def start(message: types.Message):
    user_state[message.from_user.id] = {}
    await message.answer(
        "Assalomu alaykum! \nPedagogika imtihon trenažyori\n100 ta savol tayyor!\n\nYangi savol oling:",
        reply_markup=get_menu()
    )

@dp.callback_query(F.data == "new")
async def new_question(query: CallbackQuery):   # <--- bu yerda CallbackQuery


    q = random.choice(all_questions)
    user_state[query.from_user.id] = {"last_q": q}
    savol_raqami = all_questions.index(q) + 1
    await query.message.edit_text(
        f"**Savol {savol_raqami}/100**\n\n{q['savol']}\n\nJavobni ko‘rish uchun tugmani bosing",
        reply_markup=get_menu(),
        parse_mode="Markdown"
    )
    await query.answer()

@dp.callback_query(F.data == "answer")
async def show_answer(query: CallbackQuery):     # <--- bu yerda ham CallbackQuery
    user_id = query.from_user.id
    if user_id not in user_state or "last_q" not in user_state[user_id]:
        await query.answer("Avval yangi savol oling!", show_alert=True)
        return
    javob = user_state[user_id]["last_q"]["javob"]
    await query.message.edit_text(
        f"**Javob:**\n\n{javob}\n\nYana savol olish uchun tugmani bosing",
        reply_markup=get_menu(),
        parse_mode="Markdown"
    )
    await query.answer()

